mapper.update:
UPDATE users
user_name = #{userName}, user_account = #{userAccount}, user_password = #{userPassword},
WHERE user_id = #{userId}

UPDATE users
SET followers = followers + 1
WHERE user_id = #{userId}

UPDATE users
SET followers = followers - 1
WHERE user_id = #{userId}

UPDATE users
SET following_users = following_users + 1
WHERE user_id = #{userId}

UPDATE users
SET following_users = following_users - 1
WHERE user_id = #{userId}

UPDATE users
SET following_shops = following_shops + 1
WHERE user_id = #{userId}

UPDATE users
SET following_shops = following_shops - 1
WHERE user_id = #{userId}
